package repl_IT._04_Loops;import java.util.Scanner;public class _09_Party {    public static void main(String[] args) {        Scanner input = new Scanner(System.in);        String answer = "yes";        String name = "";        do {            System.out.println("Please enter the guest's name:");            name += input.next();            System.out.println("Do you want to enter another guest's name?");            answer = input.next();            if (answer.equals("yes")){                name+=", ";            }        } while (answer.equals("yes"));        System.out.println("Guests' list: "+name);    }}