package repl_IT._04_Loops;import java.util.Scanner;public class _11_CatDogCountEqualOrNot {    public static void main(String[] args) {        Scanner scan = new Scanner(System.in);        String str = scan.nextLine();        String cOd = "";        int countCat = 0;        int countDog = 0;        for (int i = 0; i < str.length()-2 ; i++) {            cOd = "" + str.charAt(i) + str.charAt(i+1) + str.charAt(i + 2);            if (cOd.equals("cat")) {                countCat++;            }            if (cOd.equals("dog")){                countDog++;            }        }        if (countCat==countDog){            System.out.println(true);        }else{            System.out.println(false);        }        /*        ### Use a `loop` to count how many times the characters `cat` and `dog` are found in the given String `str`. Output `true` if `cat` and `dog` appear the same number of times, otherwise output `false`Main topics: loops, primitive datatypes, operators, Scanner, String, if statements```Ex:  Input:    catdog  Output:    true``````Ex:  Input:    catcat  Output:    false``````Ex:  Input:    cat-cheetah-dog-cat  Output:    false```         */    }}