package repl_IT._05_Arrays;import java.util.Scanner;public class _12_BiggestWord {    public static void main(String[] args) {        String[] words = {"aaa", "bbbbb", "whasstupppp", "longg", "jaaaaavvaaaaaaaaaa"};        String LongestWord = "";        for (String each : words) {            if (each.length()>LongestWord.length()){                LongestWord = each;            }        }        System.out.println(LongestWord);    }}