package repl_IT._05_Arrays;public class _13_ReverseSentence {    public static void main(String[] args) {        String sentence = "Java is fun";        String[] reverse = sentence.split(" ");        for (int i = reverse.length - 1; i >= 0; i--) {            if (i == 0) {                System.out.print(reverse[i] );            }else{                System.out.print(reverse[i] + " ");            }        }    }}